    function syncPhoneToActiveSlide(swiper) {
        const container = document.querySelector(".page8 .insta-slide-container");
        const phone = container?.querySelector(".phone");
        if (!container || !phone) return;

        const activeSlide = swiper.slides[swiper.activeIndex];
        const target = activeSlide.querySelector("img");
        if (!target) return;

        const cRect = container.getBoundingClientRect();
        const tRect = target.getBoundingClientRect();

        // 🔧 phone 베젤 보정값 (이 값이 핵심)
        const bezel = 8.5;   // ← 여기 숫자만 미세조정하면 끝

        phone.style.left   = `${tRect.left - cRect.left - bezel}px`;
        phone.style.top    = `${tRect.top  - cRect.top  - bezel }px`;
        phone.style.width  = `${tRect.width  + bezel * 2}px`;
        phone.style.height = `${tRect.height + bezel * 2}px`;

        phone.style.transform = "none";
    }
